import axios from "axios";

// 1. Axios Setup
const baseURL = "https://api.escuelajs.co/api/v1";
const axiosInstance = axios.create({ baseURL });

// 4. URL constants
const API_ENDPOINTS = {
  PRODUCTS: "/products/",
  // Add more endpoints as needed
};

// 2. Interceptors
axiosInstance.interceptors.request.use(
  (config) => {
    // Add any request headers here
    config.headers["Content-Type"] = "application/json";
    // We can add authentication token here if required
    // config.headers['Authorization'] = `Bearer ${getToken()}`;
    return config;
  },
  (error) => Promise.reject(error)
);

axiosInstance.interceptors.response.use(
  (response) => response,
  (error) => {
    // 5. Status Codes
    if (error.response) {
      switch (error.response.status) {
        case 400:
          console.error("Bad Request");
          break;
        case 401:
          console.error("Unauthorized");
          // Handle unauthorized access (e.g., redirect to login)
          break;
        case 404:
          console.error("Not Found");
          break;
        case 500:
          console.error("Internal Server Error");
          break;
        default:
          console.error(`Unexpected error: ${error.response.status}`);
      }
    }
    return Promise.reject(error);
  }
);

// 3. API Service
const apiService = {
  get: (url, config = {}) => axiosInstance.get(url, config),
  //   post: (url, data, config = {}) => axiosInstance.post(url, data, config),
  //   put: (url, data, config = {}) => axiosInstance.put(url, data, config),

  // Specific API methods
  getProducts: () => apiService.get(API_ENDPOINTS.PRODUCTS),
  //   getProductById: (id) => apiService.get(`${API_ENDPOINTS.PRODUCTS}/${id}`),
  //   createProduct: (data) => apiService.post(API_ENDPOINTS.PRODUCTS, data),
  //   updateProduct: (id, data) =>
  //     apiService.put(`${API_ENDPOINTS.PRODUCTS}/${id}`, data),
};

export default apiService;
